package com.backend.pruebaTecnica.controllers;import java.util.ArrayList;import java.util.List;import org.springframework.http.ResponseEntity;import org.springframework.stereotype.Controller;import org.springframework.web.bind.annotation.GetMapping;import org.springframework.web.bind.annotation.ResponseBody;import org.springframework.web.bind.annotation.RestController;import com.backend.pruebaTecnica.exceptions.HandleClientException;import com.backend.pruebaTecnica.models.User;import com.backend.pruebaTecnica.services.RequestUser;import com.backend.pruebaTecnica.services.UserService;@RestControllerpublic class UserController {
		RequestUser service = new UserService();		@GetMapping("/users/all")	public ResponseEntity<List<User>> hello() {		List<User> users = new ArrayList<User>();		try {			users = service.getUsers();		} catch (HandleClientException e) {			return ResponseEntity.status(500).body("Error to the request user, try again please").internalServerError().build();		}		return ResponseEntity.ok(users);	}
}
